	src:"img/product.png",
},
{
	id:"12",
	description:"iPhone-12 ",
	price:"BDT-180000",
	src:"img/product.png",
},
]
 

const Shop = () => {
	
	return (
		<>
			<ContentWrapper>
				<Container fluid>
					<div className="video-block section-padding">
						<Row>
                        <Col md={12}>
								<SectionHeader heading="Shop" />
							</Col>
                            
							<Col md={12} className="py-3">
                               <Row>
								   <Col md={4}>
								   <div className="card-category-item mt-0 mb-0">
		 
		 <a>
			  
			 
			 <h5>   description   </h5>
			 <h6>
		   price 
			 </h6>
			 <button className="btn btn-sm btn-success">Add to Cart</button>
		 </a>
	 </div>
			 
								   </Col>
							   </Row>
								</Col>
								

								  {
								 
								 products.map((product)=>(

									<Col xl={3} sm={6} className="mb-3" 	key={product.id} >
									
									<ShopCart
								
								product={product}
									/>
									  </Col>

								  ))} 
								
						</Row>
					</div>
					<Paginate />
				</Container>
				<ThinFooter />
			</ContentWrapper>
		</>
	);
};

export default Shop;
