import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import api from "../../../Api/Api";
import { useEffect,useState } from "react"; 
import Slider from "react-slick";


const ProductCard = (props) => {

   
	
  const [category, setCategories] = useState([]);

	useEffect(() => {
	  fetchCategories();
	}, []);
  
	let fetchCategories = () => {
	  api
		.get("products/categories", {
		  per_page: 20,
		})
		.then((response) => {
		  if (response.status === 200) {
        setCategories(response.data);
		  }
		})
		.catch((error) => {});
	};
     
   const { id,store, images,name,regular_price,sale_price}=props.product
  const productCards=<div className="card-category-items mt-0 mb-0">
  <a>
 <div className="d-flex justify-content-center">

   <div>

    <img className="img-fluid" style={{background:'none'}} src={images[0].src} alt="" />
    </div>
    <div   className="p-3">


    <h5 style={{fontSize:'15px'}}> {name} </h5>
    <h6>{
  sale_price==0?<span>{ `৳ ${regular_price}`}  </span>:<span> <strong> { `৳ ${sale_price} `} </strong> <strong style={{textDecoration:'line-through'}}> { `৳ ${regular_price}`}</strong> </span>    
  
}</h6>
    <button className="btn btn-sm btn-success">Add to Cart</button>
    </div>

   
   </div> 
   </a>
</div> 

   if(props.shop==store.shop_name){
    return  productCards
    } 
   
 

  return (
     
    <>
     
   
        
    
    
  
 {
     productCards
    
 }
    
        
    
          
         
    </>
  );
};




export default ProductCard;
