import {Container,Row,Col} from 'react-bootstrap'
import { CommentMedia, CustomMedia } from "./CommentMedia";
import ShopCart from '../Shop/ShopCard';
import BasicShop from '../Shop/BasicShop';
import VideoBlock from '../Homepage/VideoBlock';
import vedoreViedo from '../../Api/VendorVideo';
 
export default function RelatedVideo() {
	return (
		<>
			 
             <div className="video-block section-padding ">
				<Row>
					 


			 
                <Col xl={3} sm={6} className="mb-3" >
                 
               {  vedoreViedo.map((vendor)=>{
                   <RelatedVideoCard key={vendor.id} vendor={vendor} />
               })}
                </Col>
              
					 
				</Row>
			</div>
					 
				 
		  
		</>
	);
}


const RelatedVideoCard=(props)=>{
    const { title,time,videoTitleHref,url,shopUrl,videoTitle,videoCategories,shop,views,timeAgo,video,iconHref,imgAlt,imgHref,imgSrc}=props.vendorVideo;
 
 

	
	return (
		<>
	
	        
			<div className="video-card">
			 
				<div className="video-card-image">
					<Link className="play-icon" to={iconHref}>
						<FontAwesomeIcon icon={faPlayCircle} />
					</Link>
					<Link to={imgHref}>
						<img className="img-fluid" src={imgSrc} alt={imgAlt} />
					</Link>
				 
				</div>
				<div className="video-card-body">

					<div className="video-title">
						<Link to={videoTitleHref}  >{title}</Link>
					</div>

					<div
						
					> 
					<a style={{color:'#28a745', fontWeight:'700'}}  href={shopUrl}>	{  shop}  </a>
						    
					</div>
					<div className="video-view">
						{views} views &nbsp;
						<FontAwesomeIcon icon={faCalendarAlt} /> {timeAgo} ago
					</div>
					<div>
						      
					</div>
					<hr/>
					<div style={{  margin:'5px 0px'}}>
					<Row>
                       <Col>
					  <ProductSlide shop={shop}/>
					   </Col>
					  </Row>
					</div>
				</div>
			</div>
		</>
	);
}
