import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "./shop.css";
import ThinFooter from "../Footer/ThinFooter";
import SectionHeader from "../Atomics/SectionHeader/SectionHeader";
import ContentWrapper from "../Atomics/ContentWrapper/ContentWrapper";
import ShopCart from "./ShopCard";
import Paginate from "../Atomics/Paginate/Paginate";
import WooCommerceRestApi from "@woocommerce/woocommerce-rest-api";
 
import { useEffect,useState } from "react";

 


const api = new WooCommerceRestApi({
	url: "https://hulusthul.live/",
	consumerKey: "ck_9d96cef2573a0fcf41f1876c2ba7c3d7f546a163",
	consumerSecret:"cs_1261bef28ae9fe3e8bdca1648ba2c4097464ea04",
	wpAPI:true,
	version: "wc/v3",
  });

 


const BasicShop = () => {

  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  let fetchProducts = () => {
    api
      .get("products", {
        per_page: 20,
      })
      .then((response) => {
        if (response.status === 200) {
          setProducts(response.data);
        }
      })
      .catch((error) => {});
  };
  return (
    <>
     
       
               
{
  console.log("from products page:", products.name)
}

              {products.map((product) => (
                <Col xl={3} sm={6} className="mb-3" key={product.id}>
                  <ShopCart product={product} />
                  
                </Col>
              ))}
            
       
           
      
    </>
  );
};

export default BasicShop ;
