const ProductCard = (props) => {
 
     
   const { id,store, images,name,regular_price,sale_price,permalink}=props.product



 
 

  return (
     
    <>
   

       <div className="card-category-items mt-0 mb-0" style={{border:'1px solid white'}}>
     <a href={permalink}>
    <div className="">
 
      <div>
 
       <img className="img-fluid" style={{background:'none'}} src={images[0]==null? 'img/product.png':images[0].src} alt="" />
       </div>
       <div   className="p-3">
 
 
       
       <h5 style={{fontSize:'12px'}}>{
     sale_price==0?<span>{ `৳ ${regular_price}`}  </span>:<span> <strong> { `৳ ${sale_price} `} </strong> <strong style={{textDecoration:'line-through'}}> { `৳ ${regular_price}`}</strong> </span>    
     
   }</h5>
        
       </div>
   
      
      </div> 
      </a>
   </div>
    
 
    
        
    
          
         
    </>
  );
};




export default ProductCard;
