import { useEffect,useState } from "react";
import './shop.css'

const ShopCart=(props)=> {
	const {id,name,images,regular_price,sale_price}=props.product
	  
	return (
		<>
		 
			<div className="card-category-item mt-0 mb-0">
			
				<a>
					<img key={id} className="img-fluid" src={images[0].src} alt="" />
					 
				    <h5> { name} </h5>
					<h6>
				    {
						sale_price ==null ? <span> { `৳ ${regular_price}`}  </span>  :  <span > { `৳ ${sale_price}`}  </span>    
					}
				    
						  
					</h6>
					 
					<button className="btn btn-sm btn-success">Add to Cart</button>
				</a>
			</div>
		</>
	);
}
export default ShopCart;
