import "./VideoPage.css";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import ThinFooter from "../Footer/ThinFooter";
import ContentWrapper from "../Atomics/ContentWrapper/ContentWrapper";
import VideoProduct from "./VideoProduct";
import SingleVideoRight from "./SingleVideoRight";
import SingleVideoLeft from "./SingleVideoLeft";
import VideoTitle from "./VideoTitle";
import api from "../../Api/Api";
import { useEffect,useState } from "react";
 

const VideoPageSix = ({vendorVideo}) => {
	 

	const [products, setProducts] = useState([]);
	
	 

  useEffect(() => {
    fetchProducts();
  }, []);

   
  let fetchProducts = () => {
    api
      .get("products", {
        per_page: 20,
      })
      .then((response) => {
        if (response.status === 200) {
          setProducts(response.data);
        }
      })
      .catch((error) => {});
  };
	return (
		<>
		{
			console.log(`this is vendor from video page ${vendorVideo}`)
		}
			<ContentWrapper>
				<Container fluid>
					<div className="video-block-right-list section-padding"  >
						<Row className="mb-4"  >
							<Col md={8} className="smFixedVideo"    >
								<div className="single-video"  >
									
									
							 
								
									  

										  <iframe width="100%" height="315px" 
										   src="https://www.youtube.com/embed/Ar_9Iz4I2_0"
										    title="YouTube video player"
											 frameborder="0" 
											 allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
											 allowfullscreen>

											 </iframe>
										
											
								</div>
								<VideoTitle
				          	title="new mobile phone price in BD 2021ðŸ”¥smartphone price in Bangladesh 2021ðŸ“±Dhaka BD Vlogs"
					       views="62,754"
					      
			          	/>

			 
								 
							</Col>

							<Col md={4}  className="leftItems" >
							<div className="video-slider-right-list"     >
							{products.map((product) => (
        
				  <VideoProduct
					 product={product} 
				  />
                  
                 
              ))}
			  </div>			 
							</Col>
						</Row>
					</div>

					<div className="video-block section-padding sm-position" >
						<Row>
							<Col md={8} className='tagItem' >
						 
								<SingleVideoLeft />
								 
							</Col>

							<Col md={4}>
								<SingleVideoRight />
							</Col>
						</Row>
					</div>
				</Container>
				<ThinFooter />
			</ContentWrapper>
		</>
	);
};

export default VideoPageSix;
