import Slider from "react-slick";
import ProductCard from "./ProductCard";
import api from "../../../Api/Api";
import { useEffect,useState } from "react";



export default function ProductSlide() {

	const [products, setProducts] = useState([]);

	useEffect(() => {
	  fetchProducts();
	}, []);
  
	let fetchProducts = () => {
	  api
		.get("products", {
		  per_page: 20,
		})
		.then((response) => {
		  if (response.status === 200) {
			setProducts(response.data);
		  }
		})
		.catch((error) => {});
	};
	var settings = {
		dots: false,
		infinite: true,
		speed: 500,
		slidesToShow: 1,
		slidesToScroll: 1,
		initialSlide: 0,
		swipeToSlide: true,
		autoplay: true,
		autoplaySpeed: 2000,
		 
		 
		responsive: [
			{
				breakpoint: 1200,
				settings: {
					slidesToShow: 2,
					slidesToScroll: 1,
					initialSlide: 1,
				},
			},
			{
				breakpoint: 1000,
				settings: {
					slidesToShow: 1,
					slidesToScroll: 1,
					initialSlide: 1,
				},
			},
			{
				breakpoint: 600,
				settings: {
					slidesToShow: 1,
					slidesToScroll: 1,
					initialSlide: 1,
				},
			},
		],
	};
	return (
		<>
			<Slider {...settings}>
                      {
						  products.map((products)=>{
							<div className="card-category-items mt-0 mb-0" >
							<a>
						   <div className="d-flex justify-content-center">
					   
							 <div>
				
							  <img className="img-fluid" src="img/product.png" alt="" />
							  </div>
							  <div   className="p-3">
					   
				
							  <h5 style={{fontSize:'15px'}}>{products.name} </h5>
							  <h6>BDT-12000000</h6>
							  <button className="btn btn-sm btn-success">Add to Cart</button>
							  </div>
						  
							 
							 </div> 
							 </a>
						  </div>
							
						  })
					  }
				
				 
				 
				
               { /* <ProductCard/>
                 <ProductCard/>
                 <ProductCard/>
			   <ProductCard/> */}
			</Slider>
		</>
	);
}
